import React, { useEffect, useRef } from "react";

export default function Cursor() {
  const cursorRef = useRef(null);

  useEffect(() => {
    const cursor = cursorRef.current;
    if (!cursor) return;

    const moveCursor = (e) => {
      cursor.style.left = e.clientX + "px";
      cursor.style.top = e.clientY + "px";
    };

    window.addEventListener("mousemove", moveCursor);
    return () => window.removeEventListener("mousemove", moveCursor);
  }, []);

  // Define keyframes in JS for pulseGlow animation
  useEffect(() => {
    const styleSheet = document.styleSheets[0];

    const keyframes = `
      @keyframes pulseGlow {
        0%, 100% {
          box-shadow:
            0 0 6px #ff4d6d,
            0 0 12px #ff2a44,
            0 0 20px #ff1a2a;
          transform: translate(-50%, -50%) scale(1);
        }
        50% {
          box-shadow:
            0 0 12px #ff7390,
            0 0 22px #ff4160,
            0 0 30px #ff2a44;
          transform: translate(-50%, -50%) scale(1.15);
        }
      }
    `;

    // Insert the keyframes rule if it doesn't exist
    if (styleSheet.cssRules.length === 0) {
      styleSheet.insertRule(keyframes, styleSheet.cssRules.length);
    }
  }, []);

  const cursorStyle = {
    position: "fixed",
    width: "30px",
    height: "30px",
    background: "radial-gradient(circle, #ff4d6d 0%, #d4002a 70%)",
    borderRadius: "50%",
    pointerEvents: "none",
    transform: "translate(-50%, -50%)",
    zIndex: 9999,
    boxShadow:
      "0 0 8px #ff4d6d, 0 0 15px #ff2a44, 0 0 25px #ff1a2a",
    animation: "pulseGlow 2.5s ease-in-out infinite",
    transition: "left 0.1s ease, top 0.1s ease",
    left: 0,
    top: 0,
  };

  return <div ref={cursorRef} style={cursorStyle} />;
}
